import React from 'react';

export default function Features() {
  return (
    <div>
      <h2>Features</h2>
      <ul>
        <li><strong>Text-to-Speech (TTS) & Speech-to-Text (STT):</strong> Multilingual voice interaction for hands-free workflow.</li>
        <li><strong>Real-Time Audio Monitoring:</strong> Listen and analyze your music as you produce.</li>
        <li><strong>AI Vocals:</strong> Generate, tune, and customize singing/rapping voices in any style, language, or attitude.</li>
        <li><strong>Lyrics Generation:</strong> Get creative lyrics, hooks, and themes for any genre or mood.</li>
        <li><strong>Mixing/Mastering Assistant:</strong> Receive advice for EQ, compression, and effects.</li>
        <li><strong>Collaboration Tools:</strong> Work with other producers, songwriters, and artists in real time.</li>
        <li><strong>Learning Mode:</strong> Access tutorials, tips, and guided workflows for music production.</li>
        <li><strong>Plugin Marketplace:</strong> Discover and install new plugins directly from the app.</li>
        <li><strong>Customizable UI:</strong> Personalize your workspace with themes and layouts.</li>
      </ul>
    </div>
  );
}
