import React, { useState } from 'react';

export default function Lyrics() {
  const [genre, setGenre] = React.useState('pop');
  const [theme, setTheme] = React.useState('love');
  const [attitude, setAttitude] = React.useState('confident');
  const [userLyrics, setUserLyrics] = React.useState('');
  const [response, setResponse] = React.useState('');
  const [loading, setLoading] = React.useState(false);

  const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || '';

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await fetch(`${BACKEND_URL}/lyrics/generate`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ genre, theme, attitude, userLyrics })
        // No credentials sent
      });
      const data = await res.json();
      setResponse(`Status: ${data.status}, Lyrics: ${data.lyrics}`);
    } catch (err) {
      setResponse('Error generating lyrics');
    }
    setLoading(false);
  };

  return (
    <div>
      <h2>Lyrics Generation</h2>
      <p>
        Generate lyrics for any genre, mood, or style. Provide your own lyrics, specify hooks, themes, and attitude, or let the AI suggest creative ideas.
      </p>
      <ul>
        <li>Genre and style selection</li>
        <li>Hooks and song themes</li>
        <li>Attitude and mood options</li>
        <li>Paste your own lyrics or use AI suggestions</li>
      </ul>
      <form style={{marginTop: '2rem'}} onSubmit={handleSubmit}>
        <label>Genre:</label>
        <select name="genre" value={genre} onChange={e => setGenre(e.target.value)}>
          <option value="pop">Pop</option>
          <option value="hip-hop">Hip-Hop</option>
          <option value="rock">Rock</option>
          <option value="jazz">Jazz</option>
          <option value="country">Country</option>
          <option value="electronic">Electronic</option>
          <option value="other">Other</option>
        </select>
        <label style={{marginLeft: '1rem'}}>Theme:</label>
        <input type="text" name="theme" value={theme} onChange={e => setTheme(e.target.value)} placeholder="e.g. love, party" />
        <label style={{marginLeft: '1rem'}}>Attitude:</label>
        <select name="attitude" value={attitude} onChange={e => setAttitude(e.target.value)}>
          <option value="confident">Confident</option>
          <option value="chill">Chill</option>
          <option value="romantic">Romantic</option>
          <option value="aggressive">Aggressive</option>
          <option value="sad">Sad</option>
          <option value="other">Other</option>
        </select>
        <label style={{marginLeft: '1rem'}}>Paste Lyrics:</label>
        <textarea name="userLyrics" value={userLyrics} onChange={e => setUserLyrics(e.target.value)} placeholder="Paste your lyrics here" rows={2} style={{verticalAlign: 'middle'}} />
        <button type="submit" style={{marginLeft: '1rem'}} disabled={loading}>{loading ? 'Generating...' : 'Generate Lyrics'}</button>
      </form>
      {response && <div style={{marginTop: '1rem'}}>{response}</div>}
    </div>
  );
}
