import React, { useState } from 'react';

export default function Learning() {
  const [topic, setTopic] = React.useState('mixing vocals');
  const [response, setResponse] = React.useState('');
  const [loading, setLoading] = React.useState(false);

  const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || '';

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await fetch(`${BACKEND_URL}/learning/tutorial`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ topic }),
      });
      const data = await res.json();
      setResponse(`Status: ${data.status}, Tutorial: ${data.tutorial}`);
    } catch (err) {
      setResponse('Error requesting tutorial');
    }
    setLoading(false);
  };

  return (
    <div>
      <h2>Learning Mode</h2>
      <p>
        Access tutorials, tips, and guided workflows for music production. Learn new skills, explore genres, and improve your creative process.
      </p>
      <ul>
        <li>Step-by-step tutorials</li>
        <li>Tips for mixing, mastering, and songwriting</li>
        <li>Guided workflows for beginners and pros</li>
        <li>Genre exploration and skill development</li>
      </ul>
      <form style={{marginTop: '2rem'}} onSubmit={handleSubmit}>
        <label>Request Tutorial:</label>
        <select name="topic" value={topic} onChange={e => setTopic(e.target.value)}>
          <option value="mixing vocals">Mixing Vocals</option>
          <option value="mastering">Mastering</option>
          <option value="songwriting">Songwriting</option>
          <option value="arrangement">Arrangement</option>
          <option value="production">Production</option>
          <option value="genre exploration">Genre Exploration</option>
          <option value="other">Other</option>
        </select>
        <button type="submit" style={{marginLeft: '1rem'}} disabled={loading}>{loading ? 'Requesting...' : 'Get Tutorial'}</button>
      </form>
      {response && <div style={{marginTop: '1rem'}}>{response}</div>}
    </div>
  );
}
