import React, { useState } from 'react';

export default function DAWIntegration() {
  const [selectedDAW, setSelectedDAW] = useState('FL Studio');
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);
  const BACKEND_URL = 'https://vigilant-space-dollop-975x676xv4qvhp6jp-5000.app.github.dev';

  const handleConnect = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await fetch(`${BACKEND_URL}/daw/connect`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ daw: selectedDAW }),
      });
      const data = await res.json();
      setResponse(`Status: ${data.status}, DAW: ${data.daw}`);
    } catch (err) {
      setResponse('Error connecting to DAW');
    }
    setLoading(false);
  };

  return (
    <div>
      <h2>DAW Integration (Optional)</h2>
      <p>
        DAW connection is optional. You can use all features without connecting a DAW. If you want seamless workflow and real-time AI assistance, connect your favorite DAW (FL Studio, Ableton Live, Logic Pro, Cubase, Reaper, and more).
      </p>
      <ul>
        <li>Auto-detect DAW or select manually</li>
        <li>Plugin and protocol support (MIDI, OSC, VST, AU)</li>
        <li>Real-time monitoring and advice</li>
        <li>DAW-agnostic compatibility</li>
      </ul>
      <form style={{marginTop: '2rem'}} onSubmit={handleConnect}>
        <label htmlFor="daw-select">Select your DAW:</label>
        <select id="daw-select" value={selectedDAW} onChange={e => setSelectedDAW(e.target.value)}>
          <option value="FL Studio">FL Studio</option>
          <option value="Ableton Live">Ableton Live</option>
          <option value="Logic Pro">Logic Pro</option>
          <option value="Cubase">Cubase</option>
          <option value="Reaper">Reaper</option>
          <option value="Other">Other</option>
        </select>
        <button type="submit" style={{marginLeft: '1rem'}} disabled={loading}>{loading ? 'Connecting...' : 'Connect'}</button>
      </form>
      {response && <div style={{marginTop: '1rem'}}>{response}</div>}
    </div>
  );
}
