import React, { useState } from 'react';

export default function AIVocals() {
  const [gender, setGender] = React.useState('male');
  const [language, setLanguage] = React.useState('en-US');
  const [style, setStyle] = React.useState('pop');
  const [response, setResponse] = React.useState('');
  const [loading, setLoading] = React.useState(false);

  const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || '';

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await fetch(`${BACKEND_URL}/vocals/generate`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ gender, language, style }),
      });
      const data = await res.json();
      setResponse(`Status: ${data.status}, Options: ${JSON.stringify(data.options)}`);
    } catch (err) {
      setResponse('Error generating vocals');
    }
    setLoading(false);
  };

  return (
    <div>
      <h2>AI Vocals</h2>
      <p>
        Create, tune, and customize AI singing or rapping voices. Choose gender, language, ethnicity, attitude, and vocal style for full creative control.
      </p>
      <ul>
        <li>Male/female voices</li>
        <li>Multilingual and multi-ethnic options</li>
        <li>Attitude and style selection (confident, chill, opera, rap, etc.)</li>
        <li>Vocal tuning and effects</li>
      </ul>
      <form style={{marginTop: '2rem'}} onSubmit={handleSubmit}>
        <label>Voice Gender:</label>
        <select name="gender" value={gender} onChange={e => setGender(e.target.value)}>
          <option value="male">Male</option>
          <option value="female">Female</option>
          <option value="other">Other</option>
        </select>
        <label style={{marginLeft: '1rem'}}>Language:</label>
        <select name="language" value={language} onChange={e => setLanguage(e.target.value)}>
          <option value="en-US">English (US)</option>
          <option value="en-GB">English (UK)</option>
          <option value="es-ES">Spanish</option>
          <option value="fr-FR">French</option>
          <option value="de-DE">German</option>
          <option value="zh-CN">Chinese</option>
          <option value="other">Other</option>
        </select>
        <label style={{marginLeft: '1rem'}}>Style:</label>
        <select name="style" value={style} onChange={e => setStyle(e.target.value)}>
          <option value="pop">Pop</option>
          <option value="rap">Rap</option>
          <option value="opera">Opera</option>
          <option value="rock">Rock</option>
          <option value="jazz">Jazz</option>
          <option value="other">Other</option>
        </select>
        <button type="submit" style={{marginLeft: '1rem'}} disabled={loading}>{loading ? 'Generating...' : 'Generate Vocal'}</button>
      </form>
      {response && <div style={{marginTop: '1rem'}}>{response}</div>}
    </div>
  );
}
